  import axios from "axios";
    import React, { useState, useEffect, useRef } from "react";
    import { useParams } from "react-router-dom";
  import {Button, Card, Col, Container, Form, Row} from "react-bootstrap";
    export default  updateEvents = () => {
      const initialMenuState = {
        id: null,
        name: "",
        description: "",
        price: 0,
      };
      let { id } = useParams();
      const [currentMenu, setCurrentMenu] = useState(initialMenuState);
      const [submitted, setSubmitted] = useState(false);
      const countRef = useRef(0);
      useEffect(() => {
        retrieveMenu();
      }, [countRef]);
      const handleMenuChange = (e) => {
        const { name, value } = e.target;
        setCurrentMenu({ ...currentMenu, [name]: value });
      };
      const retrieveMenu = () => {
        axios
          .get(`${baseURL}/menu/${id}/`, {
            headers: {
              headers,
            },
          })
          .then((response) => {
            setCurrentMenu({
              id: response.data.id,
              name: response.data.name,
              description: response.data.description,
              price: response.data.price,
            });
            console.log(currentMenu);
          })
          .catch((e) => {
            console.error(e);
          });
      };
      const updateMenu = () => {
        let data = {
          name: currentMenu.name,
          description: currentMenu.description,
          price: currentMenu.price,
        };
        axios
          .put(`${baseURL}/menu/${id}/`, data, {
            headers: {
              headers,
            },
          })
          .then((response) => {
            setCurrentMenu({
              id: response.data.id,
              name: response.data.name,
              description: response.data.description,
              price: response.data.price,
            });
            setSubmitted(true);
            console.log(response.data);
          })
          .catch((e) => {
            console.error(e);
          });
      };
      const newMenu = () => {
        setCurrentMenu(initialMenuState);
        setSubmitted(false);
      };
      return (
                                    <Container style={{  marginLeft: "auto", marginRight: "auto" , marginTop: "100px", marginBottom: "auto"}}>

        <Card>
            <Card.Body>
    <Card.Title>Events</Card.Title>
    <Card.Subtitle className="mb-2 text-muted">Create an event! -
            <Card.Link href="/">Back</Card.Link>
        </Card.Subtitle>
  </Card.Body>
            <Card.Body>
                <Form>

  <Form.Group className="mb-3" >
    <Form.Label>Name</Form.Label>
    <Form.Control placeholder="Enter your Name" name="name" value={this.state.name} onChange={this.onChange}/>
  </Form.Group>


  <Row className="mb-3">
    <Form.Group as={Col} >
      <Form.Label>Location</Form.Label>
      <Form.Control  placeholder="Enter your Location" name="location" value={this.state.location} onChange={this.onChange}/>
    </Form.Group>
         <Form.Group as={Col} >
      <Form.Label>Date</Form.Label>
      <Form.Control type="datetime-local" name="date" value={this.state.date} onChange={this.onChange}/>
    </Form.Group>
  </Row>
                    <div align="right" >
                        <Button onClick={this.handleSubmit}>submit</Button> </div>

                </Form>

            </Card.Body>
</Card>

     </Container>

      );
    };
