import React, { Component } from 'react'
import Table from 'react-bootstrap/Table'
import Container from 'react-bootstrap/Container'
import Button from 'react-bootstrap/Button'
import {Card} from "react-bootstrap";
const axios = require('axios');
export default class EventList extends Component {
state = {
    isLoading: true,
    employee: [],
    error: null
}
constructor(props) {
    super(props);
}
fetchEmp() {
    fetch(`http://localhost:8000/api/events`)
        .then(response => response.json())
        .then(data =>
           this.setState({
               employee: data,
               isLoading: false,
           })
        )
       .catch(error => this.setState({ error, isLoading: false }));
}
componentDidMount() {
    this.fetchEmp();
}

render() {
    return (
                    <Container style={{  marginLeft: "auto", marginRight: "auto" , marginTop: "auto", marginBottom: "auto", hover: "hover"}}>

        <Card>
            <Card.Body>
    <Card.Title>Events</Card.Title>
    <Card.Subtitle className="mb-2 text-muted">List of events! -
            <Card.Link href="/createemp">Create</Card.Link>
        </Card.Subtitle>
  </Card.Body>
        <Table>
           <tbody>
            <tr>
              <th> NAME</th>
              <th> LOCATION</th>
              <th> TIME</th>
              <th>ACTION</th>
            </tr>

              {!this.state.isLoading?this.state.employee.map((user)=>{
              return (<tr key={user.id}>
                      <td>{user.name}</td><td>{user.location}</td><td>{user.date}</td><td><Button onClick={() => this.props.history.push(`/updateemp/${user.id}`)}>Update</Button>    <Button variant="danger" >Delete</Button></td></tr>
              )
              })
              :
                 "LOADING"
              }
           </tbody>
        </Table>
</Card>
     </Container>

    )
  }
}
