import React, { Component } from 'react'
import Table from 'react-bootstrap/Table'
import Container from 'react-bootstrap/Container'
import Button from 'react-bootstrap/Button'
import {Card} from "react-bootstrap";
import moment from "moment";
const axios = require('axios');
export default class EventList extends Component {
state = {
    isLoading: true,
    employee: [],
    error: null,
    //
    currentPage: 1,
    postsPerPage: 5
//
}
constructor(props) {
    super(props);
}
fetchEmp() {
    fetch(`http://localhost:8000/api/events`)
        .then(response => response.json())
        .then(data =>
           this.setState({
               employee: data,
               isLoading: false,
           })
        )
       .catch(error => this.setState({ error, isLoading: false }));
}
componentDidMount() {
    this.fetchEmp();
}

render() {
    //
     const { currentPage, postsPerPage, posts, loading } = this.state;

    const indexOfLastPost = currentPage * postsPerPage;
    const indexOfFirstPost = indexOfLastPost - postsPerPage;
    const currentPosts = posts.slice(indexOfFirstPost, indexOfLastPost);

    const paginate = pageNum => this.setState({ currentPage: pageNum });

    const nextPage = () => this.setState({ currentPage: currentPage + 1 });

    const prevPage = () => this.setState({ currentPage: currentPage - 1 });
    //
    return (
                    <Container style={{  marginLeft: "auto", marginRight: "auto" , marginTop: "100px", marginBottom: "auto"}}>

        <Card>
            <Card.Body>
    <Card.Title>Events</Card.Title>
    <Card.Subtitle className="mb-2 text-muted">List of events! -
            <Card.Link href="/createemp">Create</Card.Link>
        </Card.Subtitle>
  </Card.Body>
        <Table>
           <tbody>
            <tr>
              <th> NAME</th>
              <th> LOCATION</th>
              <th> TIME</th>
              <th>ACTION</th>
            </tr>

              {!this.state.isLoading?this.state.employee.map((user)=>{
              return (<tr key={user.id}>
                      <td>{user.name}</td>
                      <td>{user.location}</td>
                      <td>{moment(user.date).utc().format("MMM DD, YYYY - HH:mm:ss UTC")} </td>
                      <td>            <Card.Link href="" style={{textDecoration: "none"}}>Edit</Card.Link>
            <Card.Link href="" style={{textDecoration: "none"}}>Delete</Card.Link>

                          {/*<Button onClick={() => this.props.history.push(`/updateemp/${user.id}`)}>Update</Button>    <Button variant="danger" >Delete</Button>*/}
                      </td></tr>
              )
              })
              :
                 "LOADING"
              }
           </tbody>
            <Posts posts={currentPosts} loading={loading} />
        <Pagination postsPerPage={postsPerPage} totalPosts={posts.length} paginate={paginate} nextPage={nextPage} prevPage={prevPage} />

        </Table>
</Card>
     </Container>

    )
  }
}
