import React, { Component } from 'react'
import {Container, Form, Row, Col, Button, Card} from 'react-bootstrap'
import Table from "react-bootstrap/Table";
import moment from "moment";
const axios = require('axios').default;
export default class CreateEvents extends Component {
  constructor(props) {
    super(props);
    this.state = {
      emp_regno: '',
      emp_name: '',
      emp_email: '',
      emp_mobile: '',
    };
    this.handleChange = this.onChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
onChange = (e) => this.setState({ [e.target.name]: e.target.value });
handleSubmit(event) {
    axios.post('http://localhost:8000/employee/api/create', {
        emplyee_regNo: this.state.emp_regno,
        emplyee_name: this.state.emp_name,
        employee_email: this.state.emp_email,
        employee_mobile: this.state.emp_mobile
    }).then(function (response) {
        console.log(response);
    }).catch(function (error) {
        console.log(error);
    });
    event.preventDefault();
}
render() {
  return (
                          <Container style={{  marginLeft: "auto", marginRight: "auto" , marginTop: "100px", marginBottom: "auto"}}>

        <Card>
