            <Card.Link href="/">Back</Card.Link>
        </Card.Subtitle>
  </Card.Body>
        <Table>
           <tbody>
            <tr>
              <th> NAME</th>
              <th> LOCATION</th>
              <th> TIME</th>
              <th>ACTION</th>
            </tr>

              {!this.state.isLoading?this.state.employee.map((user)=>{
              return (<tr key={user.id}>
                      <td>{user.name}</td>
                      <td>{user.location}</td>
                      <td>{moment(user.date).utc().format("MMM DD, YYYY- HH:mm:ss UTC")} </td>
                      <td>            <Card.Link href={(`/event/update/${user.id}`)} style={{textDecoration: "none"}}>Edit</Card.Link>
            <Card.Link href="" style={{textDecoration: "none"}}>Delete</Card.Link>

                      </td></tr>
              )
              })
              :
                 "LOADING"
              }
           </tbody>
        </Table>
</Card>
     </Container>

