import axios from 'axios';
import React, {useState, useEffect} from 'react';
import {useNavigate, useParams} from 'react-router-dom';

const UpdateEvents = () => {

    let history = useNavigate();
    const { id } = useParams();

    const [name, setName] = useState(null)
    const [location, setLocation] = useState(null)
    const [date, setDate] = useState(null)


    useEffect(() => {
        loadData();
    }, [])


    // load students by its is and show data to forms by value

   let loadData = async () => {
    const result = await axios.get(`http://127.0.0.1:8000/api/events/${id}`);
    console.log(result.data.name);

    setName(result.data.name);
    setLocation(result.data.location);
    setDate(result.data.date);
   }



   const updateEvents = async () => {
        let formField = new FormData()

        formField.append('name',name)
        formField.append('location',location)
        formField.append('date',date)

        await axios({
            method: 'PUT',
            url: `http://127.0.0.1:8000/api/events/${id}/`,
            data: formField
        }).then(response => {
            console.log(response.data);
            history.push("/");
        })

    }





    return (

        <div className="container">
  <div className="w-75 mx-auto shadow p-5">
    <h2 className="text-center mb-4">Update A Student</h2>



      <div className="form-group">
        <input
          type="text"
          className="form-control form-control-lg"
          placeholder="Enter Your Name"
          name="name"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
      </div>

       <div className="form-group">
        <input
          type="text"
          className="form-control form-control-lg"
          placeholder="Enter Your Name"
          name="location"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
        />
      </div>

       <div className="form-group">
        <input
          type="datetime-local"
          className="form-control form-control-lg"
          placeholder="Enter Your Name"
          name="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
        />
      </div>


      <button onClick={updateEvents} className="btn btn-primary btn-block">Update Student</button>

  </div>
</div>

    );
};

export default UpdateEvents;
