import React, { Component } from 'react'
import Table from 'react-bootstrap/Table'
import Container from 'react-bootstrap/Container'
import Button from 'react-bootstrap/Button'
const axios = require('axios');
export default class EventList extends Component {
state = {
    isLoading: true,
    employee: [],
    error: null
}
constructor(props) {
    super(props);
}
fetchEmp() {
    fetch(`http://localhost:8000/api/events`)
        .then(response => response.json())
        .then(data =>
           this.setState({
               employee: data,
               isLoading: false,
           })
        )
       .catch(error => this.setState({ error, isLoading: false }));
}
componentDidMount() {
    this.fetchEmp();
}
render() {
    return (
        <Container style={{ marginTop: '100px' }}>
        <Button variant="secondary" style={{ float: 'right', margin: '20px' }} onClick={() => this.props.history.push('/createemp')}>Add a Employee</Button>
        <Table striped bordered hover>
          <thead>
            <tr>
              <th>ID</th>
              <th> Name</th>
              <th> location</th>
              <th> date</th>
              <th>Action</th>
            </tr>
          </thead>
           <tbody>
              {!this.state.isLoading?this.state.employee.map((user)=>{
              return (
                 <tr key={user.id}>
                   <td>{user.id}</td>
                   <td>{user.name}</td>
                   <td>{user.location}</td>
                   <td>{user.date}</td>
                   <td><Button onClick={() => this.props.history.push(`/updateemp/${user.id}`)}>Update</Button>    <Button variant="danger" >Delete</Button></td>
                 </tr>
              )
              })
              :
                 "LOADING"
              }
           </tbody>
        </Table>
     </Container>
    )
  }
}
