import axios from "axios";
import React, { useState, useEffect, useRef } from "react";
import { useParams } from "react-router-dom";

const UpdateEvents = () => {
  const initialMenuState = {
    id: null,
    name: "",
    location: "",
    date: ""
  };

  let { id } = useParams();

  const [currentMenu, setCurrentMenu] = useState(initialMenuState);
  const countRef = useRef(0);

  useEffect(() => {
    retrieveMenu();
  }, [countRef]);

  const handleMenuChange = (e) => {
    const { name, value } = e.target;
    setCurrentMenu({ ...currentMenu, [name]: value });
  };

  const retrieveMenu = () => {
    axios
        .get('http://localhost:8000/api/events/${id}')
      .then((response) => {
        setCurrentMenu({
          id: response.data.id,
          name: response.data.name,
          location: response.data.location,
          date: response.data.date,
        });
        console.log(currentMenu);
      })
      .catch((e) => {
        console.error(e);
      });
  };

  const updateMenu = () => {
    let data = {
      name: currentMenu.name,
      location: currentMenu.location,
      date: currentMenu.date,
    };

    axios
      .put(`http://127.0.0.1:8000/api/events/${id}/`, data, )
      .then((response) => {
        setCurrentMenu({
          id: response.data.id,
          name: response.data.name,
          location: response.data.location,
          date: response.data.date,
        });
        console.log(response.data);
      })
      .catch((e) => {
        console.error(e);
      });
  };

  const newMenu = () => {
    setCurrentMenu(initialMenuState);
  };

  return (
    <div className="submit-form">

        <div>
          <div className="form-group">
            <label htmlFor="name">Name</label>
            <input
              type="text"
              className="form-control"
              id="name"
              required
              value={currentMenu.name}
              onChange={handleMenuChange}
              name="name"
            />
          </div>

          <div className="form-group">
            <label htmlFor="description">Description</label>
            <input
              type="text"
              className="form-control"
              id="description"
              required
              value={currentMenu.location}
              onChange={handleMenuChange}
              name="location"

            />
          </div>

          <div className="form-group">
            <label htmlFor="price">Price</label>
            <input
              type="datetime-local"
              className="form-control"
              id="price"
              required
              value={currentMenu.date}
              onChange={handleMenuChange}
              name="price"
            />
          </div>

          <button onClick={updateMenu} className="btn btn-success">
            Submit
          </button>
        </div>
    </div>
  );
};
export default UpdateEvents
